import React, { Component } from 'react'
import './travel.scss'
//import TravelMap from '../TravelMap/TravelMap'
// import {travelmapData} from '../../travelmapData'
import MapGL, { Marker,NavigationContro, Popup } from 'react-map-gl';


const MAPBOX_TOKEN ='pk.eyJ1IjoibGF3aGVpdHVuZyIsImEiOiJjazh0dHMxNzEwNDEzM21vM2Z2M2FsbjljIn0.y1ygmbLRRSswPtVrQsMBUQ'
const markerList=[
    {'lat': 17.441013,
    'long': 78.391796,
    'name': 'ABC Hospitals',
    'info': 10},
    { 'lat':17.442889,
    'long': 78.396873,
    'name': 'XYZ Hospitals',
    'info': 20},
    {'lat': 17.441681,
    'long': 78.394357,
    'name': 'NRI Hospitals',
    'info': 10}
    ];

export default class Travel extends Component {
    constructor(props) {
        super(props);
        this.state = {
          viewport: {
            latitude: 37.8,
            longitude: -122.4,
            zoom: 2,
            bearing: 0,
            pitch: 0
          },
          popupInfo:null
        };
        this.renderPopup = this.renderPopup.bind(this)
      }

      renderPopup(){
        return this.state.popupInfo && (
          <Popup tipSize={5}
            anchor="bottom-right"
            longitude={this.state.popupInfo.state.longitude}
            latitude={this.state.popupInfo.state.latitude}
            onClose={() => this.setState({popupInfo: null})}
            closeOnClick={true}>
          </Popup>
        )
      }

      render() {
        const {viewport} = this.state;
        return (
          <MapGL
            {...this.state.viewport}
            width="80vw"
            height="60vh"
            center="center"
            mapStyle="mapbox://styles/mapbox/streets-v11"
            onViewportChange={viewport => this.setState({viewport})}
            mapboxApiAccessToken={MAPBOX_TOKEN}
          >
            <Marker longitude="37.8" latitude="-122.4">
            </Marker>
          </MapGL>

        );
      }
    }
