import React from 'react'
import './hero.scss'
import styled from 'styled-components'
import BCG from '../../lhtimg1.jpeg'
import '../../index.scss';

export default function Hero ({max,img}) {
        return (
            // <header className={hero}>
            // <h1>Hey, I'm Tiffany</h1>
            // {children}</header>
            <HeroWrapper max={max} img={img} className="defaultHero">
                <div className="banner">
                    <h1>
                        Hey, I'm Tiffany
                    </h1>
                </div>
            </HeroWrapper>

        );

}

const HeroWrapper = styled.div`
    text-align:center;
    font-family:$title;
    display:flex;
    align-items:center;
    justify-content:center;
    min-height: ${props =>props.max?'100vh':'60vh'}; 
    color: white;
    background: linear-gradient((0 0 0 0.3), (0 0 0 0.3)), url(${props => props.img}) center/cover no-repeat;
    opacity: 0.6;
    background-size:cover;
`
Hero.defaultProps ={
    img:BCG
}

